(function($){function switch_student_discount(){var label=$('[name="course_name"] option:selected').text();if(label.match(/学割適用可/)){$('.is_student').show();}else{$('.student-discount').prop('checked',false).change();$('.mw_wp_form_input .is_student').hide();}
if($('.student-discount').prop('checked')){$(".is_discount_true").show();}else{$(".mw_wp_form_input .is_discount_true").hide();}}
$(function(){switch_student_discount();$('#course').change(function(){switch_student_discount();});$('.student-discount').click(function(){switch_student_discount();});});$(function(){set_date_value();$('#course').change(function(){set_date_value();});});function set_date_value(){$.ajax({url:'/wp-content/themes/raisetech2019/api.php',type:'post',dataType:'json',data:{course:$('#course').val()}}).done(function(response){var date=$('#date').val(),selected_val=null;$('#date').children().remove();$.each(response,function(index,value){$('#date').append($("<option>").val(value).text(value));if(value==date){selected_val=value;}});if(selected_val==null){selected_val=$('#date option:eq(0)').val();}
$('#date').val(selected_val);}).fail(function(){alert('エラーが発生しました。');});}
$(window).on("scroll",function(){scrollTop=$(window).scrollTop();targetPoint=$('#Precautions').offset().top;if(scrollTop>=targetPoint-100){$(".Floating__back").fadeIn();}else{$(".Floating__back").fadeOut();}});})(jQuery);